sp_widget.client_script:
function($rootScope,$scope){  //Note the scope additions
	/* widget controller */
	var c = this;
	c.selectedItem = function(idx){
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectedNote', id);		
	}

	$rootScope.$on('updateList', function(event,data){
		console.log("listened: " + event + ' / ' + data);
		c.server.get({
			action: 'updateList',
			noteID: $rootScope.refreshID
		}).then(function(r){
			console.log(".then: c.data.notes.title: " + c.data.notes.title);
			c.data.notes.title = r.data.notes.title;
			//c.data.noteID
			//c.note.title = r.data.note.title;
			//c.note.note = r.data.note.note;
			//c.note.noteID = r.data.note.sys_id;
		});

	});


}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */

	//create an array to populate without notes
	data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();

	while(noteGR.next()){
		var noteObj = {};
		//use service portal helper method to get some (copies!) display values
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');

		//get the first 20 characters of the description
		noteObj.note = noteGR.getValue('note').slice(0,20);	

		//push the populated obj into the array
		data.notes.push(noteObj);
	}

	if(input && input.refreshID){
		var list = new GlideRecord('x_snc_createnotes_note');
		if(list.get(input.refreshID)){
			if(input.action == 'updateList'){
			var noteObj2 = {};
				$sp.getRecordDisplayValues(noteObj2, list, 'title, note, sys_id');
				noteObj2.note = list.getValue('note').slice(0,20);
				data.notes.push(noteObj2);
			}
		}
	}

})();
